mapper.insert:
INSERT INTO board
SET title = #{title}
, ctnts = #{ctnts}
, writer = #{writer}

mapper.select:
SELECT iboard, title, ctnts, writer, created_at as createdAt
FROM board
WHERE iboard = #{iboard}

mapper.update:
UPDATE board
SET title = #{title}
, ctnts = #{ctnts}
, writer = #{writer}
WHERE iboard = #{iboard}
